function GroupByOccupation() {
    const people = [
      { id: 1, name: "anh Bao", age: 18, occupation: "Software Developer" },
      { id: 2, name: "anh Vy", age: 19, occupation: "Designer" },
      { id: 3, name: "anh Thu", age: 20, occupation: "Student" },

    ];

    // Group people by their occupation
    const grouped = people.reduce((acc, person) => {
      if (!acc[person.occupation]) {
        acc[person.occupation] = [];
      }
      acc[person.occupation].push(person);
      return acc;
    }, {});

    return (
      <div>
        <h2>Grouped by Occupation</h2>
        {Object.keys(grouped).map((occupation) => (
          <div key={occupation}>
            <h3>{occupation}</h3>
            <ul>
              {grouped[occupation].map((person) => (
                <li key={person.id}>
                  {person.name} ({person.age} years old)
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    );
  }

  export default GroupByOccupation;
